// src/routes/user.routes.js
import express from 'express';
import { changePassword, createUser, getAllUsers, getUser, loginUser } from '../controllers/user.controller';
import { validationSignUp } from '../validation/auth.validate';

const router = express.Router();

router.post('/create', validationSignUp, createUser);
router.post('/login',loginUser);
router.put('/update-password', changePassword);

router.get('/all-users', getAllUsers);
router.get('/user', getUser);


export default router;

