const express = require('express');
import User from "../database/models/user";
const passport = require('passport');
const GoogleStrategy = require('passport-google-oauth20').Strategy;
const session = require('express-session');
const SequelizeStore = require('connect-session-sequelize')(session.Store);
const sequelize = require('../database/config/sequelize'); // Assuming you have Sequelize initialized in models/index.js

const router = express.Router();

// Configure SequelizeStore for session management
const sessionStore = new SequelizeStore({
  db: sequelize,
});

// Initialize session middleware
router.use(session({
  secret: process.env.SESSION_SECRET,
  resave: false,
  saveUninitialized: false,
  store: sessionStore,
}));

// Sync the sessionStore with the database
sessionStore.sync();

// Passport setup
passport.use(new GoogleStrategy({
  clientID: process.env.GOOGLE_CLIENT_ID,
  clientSecret: process.env.GOOGLE_CLIENT_SECRET,
  callbackURL: '/auth/google/redirect'
}, async (accessToken, refreshToken, profile, done) => {
  const { id, displayName, emails } = profile;
  const email = emails[0].value;

  try {
    let user = await User.findOne({ where: { googleId: id } });

    if (user) {
      return done(null, user);
    }

    user = await User.create({
      googleId: id,
      displayName,
      email,
    });

    done(null, user);
  } catch (err) {
    console.error(err);
    done(err, null);
  }
}));

passport.serializeUser((user, done) => done(null, user.id));

passport.deserializeUser(async (id, done) => {
  try {
    const user = await User.findByPk(id);
    done(null, user);
  } catch (err) {
    done(err, null);
  }
});

// Routes
router.get('/auth/google', passport.authenticate('google', { scope: ['profile', 'email'] }));

router.get('/auth/google/redirect', passport.authenticate('google', { failureRedirect: '/' }), (req, res) => {
  res.status(201).json({
    success:true, 
    message:'Successful registered.Please check your email for verification',
    user:newUser,
  });
});

router.get('/logout', (req, res) => {
  req.logout();
  res.redirect('/');
});

module.exports = router;

